INSERT INTO `directus_revisions` VALUES (1306,1415,'about','1','{\"id\": 1, \"people\": [{\"bio\": \"<p><a href=\\\"https://oliviadesalve.com/\\\" target=\\\"_blank\\\" rel=\\\"noopener\\\">Olivia de Salve Villedieu</a> is a graphic designer, art director, and aspiring construction worker based in Brooklyn, NY &amp; Houston, TX.</p>\", \"name\": \" \"}, {\"bio\": \"<p><a href=\\\"https://www.emilyannenichol.com/\\\" target=\\\"_blank\\\" rel=\\\"noopener\\\">Emily Nichol</a> is a writer and marketer based in Mexico City, Mexico, where she lives as floppily as possible.</p>\", \"name\": \" \"}, {\"bio\": \"<p><a href=\\\"https://weihaowang.work/\\\" target=\\\"_blank\\\" rel=\\\"noopener\\\">Wei-hao Wang</a> is a graphic designer and programmer currently based in Taipei, Taiwan. In college, he wore flip flops every day.</p>\", \"name\": \" \"}], \"status\": \"published\", \"background\": \"aeb34d0b-3065-48b3-ac91-e11aed8c2742\", \"description\": \"<p>The Floppy Hammer is exactly what it sounds like: a hammer made entirely out of a soft, pliable, pink silicone. Originally created as an art object by Olivia de Salve Villedieu in 2019, this useless tool flops around when you try to use it and anyone that uses it can&rsquo;t help but laugh. Earlier iterations made appearances on Olivia\'s coffee table and at her dinner parties, where they were instant conversation starters. It quickly became clear that this hammer sparks joy, and we always need a little more of that. The Floppy Hammer is the perfect housewarming gift, humorous home decoration, or unexpected addition to your tool box.</p>\", \"date_created\": \"2024-10-15T07:20:59.000Z\", \"date_updated\": \"2024-11-03T14:56:30.000Z\"}','{\"background\": \"aeb34d0b-3065-48b3-ac91-e11aed8c2742\", \"date_updated\": \"2024-11-03T14:56:30.314Z\"}',NULL,NULL),(1307,1416,'directus_files','2010e218-f82d-4c21-a676-7361533e6d4f','{\"id\": \"2010e218-f82d-4c21-a676-7361533e6d4f\", \"tags\": null, \"type\": \"image/jpeg\", \"embed\"